CREATE OR REPLACE PROCEDURE dashboard_html5_pck.obter_horas_cronograma (nr_seq_proj_analise_p bigint, nr_seq_proj_prog_p bigint, nr_seq_proj_teste_p bigint, ie_papel_executor1_p text, ie_papel_executor2_p text, ie_atividade_p text, qt_horas_prev_p INOUT bigint, qt_horas_real_p INOUT bigint, qt_horas_saldo_p INOUT bigint) AS $body$
BEGIN
        if (coalesce(ie_papel_executor1_p::text, '') = '') and (coalesce(ie_papel_executor2_p::text, '') = '') and (coalesce(ie_atividade_p::text, '') = '') then
            select sum(c.qt_hora_prev),
                   sum(c.qt_hora_real),
                   sum(c.qt_hora_saldo)
              into STRICT qt_horas_prev_p,
                   qt_horas_real_p,
                   qt_horas_saldo_p
              from proj_cronograma b,
                   proj_cron_etapa c
             where b.nr_sequencia = c.nr_seq_cronograma
               and b.nr_seq_proj in (nr_seq_proj_analise_p, nr_seq_proj_prog_p, nr_seq_proj_teste_p)
               and b.ie_situacao = 'A';
        end if;
        if ((ie_papel_executor1_p IS NOT NULL AND ie_papel_executor1_p::text <> '') or (ie_papel_executor2_p IS NOT NULL AND ie_papel_executor2_p::text <> '')) and (coalesce(ie_atividade_p::text, '') = '') then
            select sum(c.qt_hora_prev),
                   sum(c.qt_hora_real),
                   sum(c.qt_hora_saldo)
              into STRICT qt_horas_prev_p,
                   qt_horas_real_p,
                   qt_horas_saldo_p
              from proj_cronograma b,
                   proj_cron_etapa c
             where b.nr_sequencia = c.nr_seq_cronograma
               and b.nr_seq_proj in (nr_seq_proj_analise_p, nr_seq_proj_prog_p, nr_seq_proj_teste_p)
               and b.ie_situacao = 'A'
               and c.ie_papel_executor in (ie_papel_executor1_p, ie_papel_executor2_p);
        end if;
        if ((ie_papel_executor1_p IS NOT NULL AND ie_papel_executor1_p::text <> '') or (ie_papel_executor2_p IS NOT NULL AND ie_papel_executor2_p::text <> '')) and (ie_atividade_p IS NOT NULL AND ie_atividade_p::text <> '') then
            select sum(c.qt_hora_prev),
                   sum(c.qt_hora_real),
                   sum(c.qt_hora_saldo)
              into STRICT qt_horas_prev_p,
                   qt_horas_real_p,
                   qt_horas_saldo_p
              from proj_cronograma b,
                   proj_cron_etapa c
             where b.nr_sequencia = c.nr_seq_cronograma
               and b.nr_seq_proj in (nr_seq_proj_analise_p, nr_seq_proj_prog_p, nr_seq_proj_teste_p)
               and b.ie_situacao = 'A'
               and c.ie_papel_executor in (ie_papel_executor1_p, ie_papel_executor2_p)
               and c.ie_atividade = ie_atividade_p;
        end if;
    END;

$body$
