SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION detalhe_kpi_pck.detalhe_defeitos () RETURNS SETOF T_DETALHE AS $body$
DECLARE

	
	t_detalhe_row_w	t_detalhe_row;	
	
	c01 CURSOR FOR
	SELECT	distinct
		nr_sequencia,
		ds_dano_breve	
	from	os_erro_gerencia_v
	where	dt_ordem_servico 	between dt_ref_inicio_w and dt_ref_fim_w
	and	ie_ident_erro		<> 'W'
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	nr_seq_gerencia 	in (SELECT	x.nr_sequencia
		from	gerencia_wheb x
		where	x.nr_seq_diretoria	= nr_seq_diretoria_w)
	and	coalesce(nr_seq_diretoria_w,0) > 0
	
union

	select	distinct 
		nr_sequencia,
		ds_dano_breve	
	from	os_erro_gerencia_v
	where	dt_ordem_servico 	between dt_ref_inicio_w and dt_ref_fim_w
	and	ie_ident_erro		<> 'W'
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	nr_seq_gerencia 	= nr_seq_gerencia_w
	and	coalesce(nr_seq_gerencia_w,0) > 0
	
union

	select	distinct 
		nr_sequencia,
		ds_dano_breve	
	from	os_erro_gerencia_v
	where	dt_ordem_servico 	between dt_ref_inicio_w and dt_ref_fim_w
	and	ie_ident_erro		<> 'W'
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	nr_seq_grupo 		= nr_seq_grupo_w
	and	coalesce(nr_seq_grupo_w,0) > 0
	
union

	select	distinct 
		nr_sequencia,
		ds_dano_breve	
	from	os_erro_gerencia_v
	where	dt_ordem_servico 	between dt_ref_inicio_w and dt_ref_fim_w
	and	ie_ident_erro		<> 'W'
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and	coalesce(cd_pessoa_fisica_w,'X') <> 'X';

	c01_w	c01%rowtype;	
	
	
BEGIN
	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	
		t_detalhe_row_w.identificador	:= c01_w.nr_sequencia;
		t_detalhe_row_w.descricao	:= c01_w.ds_dano_breve;
		t_detalhe_row_w.detalhe		:= null;

		RETURN NEXT t_detalhe_row_w;		
	
	end loop;
	close C01;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
