CREATE OR REPLACE FUNCTION detalhe_kpi_pck.detalhe_retornos_os () RETURNS SETOF T_DETALHE AS $body$
DECLARE

	
	t_detalhe_row_w	t_detalhe_row;
	ds_detalhe_w	varchar(255);
	
	c01 CURSOR FOR	
	SELECT	a.nr_sequencia,
		c.ds_dano_breve,
		max(obter_qt_voltas_os_dev(a.nr_sequencia)) qt_voltas,
		sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) qt_voltas_filtro,
		substr(obter_desc_expressao(661447),1,255) ds_nivel
	from	os_encerrada_gerencia_v a,
		grupo_desenvolvimento b,
		man_ordem_servico c
	where	a.nr_sequencia = c.nr_sequencia
	and	exists (	SELECT	1
			from	man_ordem_log_grupo_des x
			where	x.nr_seq_ordem_serv = a.nr_sequencia
			and	x.nr_seq_grupo_des = b.nr_sequencia)
	and	exists (	select	1
			from	man_ordem_serv_estagio x,
				man_estagio_processo y
			where	x.nr_seq_estagio = y.nr_sequencia
			and	x.nr_seq_ordem = a.nr_sequencia
			and	y.ie_desenv = 'S')	
	and	b.nr_seq_gerencia 	in (select	x.nr_sequencia
			from	gerencia_wheb x
			where	x.nr_seq_diretoria	= nr_seq_diretoria_w)
	and	a.dt_fim_real between dt_ref_inicio_w and dt_ref_fim_w
	GROUP BY a.nr_sequencia,
		c.ds_dano_breve
	 HAVING	max(obter_qt_voltas_os_dev(a.nr_sequencia)) > 2
	and	sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) > 0
	
union all

	select	a.nr_sequencia,
		c.ds_dano_breve,
		max(obter_qt_voltas_os_dev(a.nr_sequencia)) qt_voltas,
		sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) qt_voltas_filtro,
		substr(obter_desc_expressao(290876),1,255) ds_nivel
	from	os_encerrada_gerencia_v a,
		grupo_desenvolvimento b,
		man_ordem_servico c
	where	a.nr_sequencia = c.nr_sequencia
	and	exists (	select	1
			from	man_ordem_log_grupo_des x
			where	x.nr_seq_ordem_serv = a.nr_sequencia
			and	x.nr_seq_grupo_des = b.nr_sequencia)
	and	exists (	select	1
			from	man_ordem_serv_estagio x,
				man_estagio_processo y
			where	x.nr_seq_estagio = y.nr_sequencia
			and	x.nr_seq_ordem = a.nr_sequencia
			and	y.ie_desenv = 'S')	
	and	b.nr_seq_gerencia = nr_seq_gerencia_w
	and	a.dt_fim_real between dt_ref_inicio_w and dt_ref_fim_w
	GROUP BY a.nr_sequencia,
		c.ds_dano_breve
	 HAVING	max(obter_qt_voltas_os_dev(a.nr_sequencia)) > 2
	and	sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) > 0
	
union all

	select	a.nr_sequencia,
		c.ds_dano_breve,
		max(obter_qt_voltas_os_dev(a.nr_sequencia)) qt_voltas,
		sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) qt_voltas_filtro,
		substr(obter_desc_expressao(291040),1,255) ds_nivel
	from	os_encerrada_gerencia_v a,
		grupo_desenvolvimento b,
		man_ordem_servico c
	where	a.nr_sequencia = c.nr_sequencia
	and	exists (	select	1
			from	man_ordem_log_grupo_des x
			where	x.nr_seq_ordem_serv = a.nr_sequencia
			and	x.nr_seq_grupo_des = b.nr_sequencia)
	and	exists (	select	1
			from	man_ordem_serv_estagio x,
				man_estagio_processo y
			where	x.nr_seq_estagio = y.nr_sequencia
			and	x.nr_seq_ordem = a.nr_sequencia
			and	y.ie_desenv = 'S')	
	and	b.nr_sequencia	= nr_seq_grupo_w
	and	a.dt_fim_real between dt_ref_inicio_w and dt_ref_fim_w
	GROUP BY a.nr_sequencia,
		c.ds_dano_breve
	 HAVING	max(obter_qt_voltas_os_dev(a.nr_sequencia)) > 2
	and	sum(obter_qt_voltas_os_dev(a.nr_sequencia, b.nr_sequencia, 'G')) > 0
	
union all

	select	a.nr_sequencia,
		c.ds_dano_breve,
		obter_qt_voltas_os_dev(a.nr_sequencia) qt_voltas,
		obter_qt_voltas_os_dev(a.nr_sequencia, nm_usuario_exec_w, 'U') qt_voltas_filtro,
		substr(obter_desc_expressao(300907),1,255) ds_nivel
	from	os_encerrada_gerencia_v a,		
		man_ordem_servico c
	where	a.nr_sequencia = c.nr_sequencia
	and	exists (	select	1
			from	man_ordem_serv_estagio x
			where	x.nr_seq_ordem = a.nr_sequencia
			and	x.nm_usuario = nm_usuario_exec_w)
	and	exists (	select	1
			from	man_ordem_serv_estagio x,
				man_estagio_processo y
			where	x.nr_seq_estagio = y.nr_sequencia
			and	x.nr_seq_ordem = a.nr_sequencia
			and	y.ie_desenv = 'S')	
	and	a.dt_fim_real between dt_ref_inicio_w and dt_ref_fim_w
	and	obter_qt_voltas_os_dev(a.nr_sequencia) > 2
	and	obter_qt_voltas_os_dev(a.nr_sequencia, nm_usuario_exec_w, 'U') > 0;
	
	c01_w	c01%rowtype;	
	
	
BEGIN
	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin	
		t_detalhe_row_w.identificador	:= c01_w.nr_sequencia;
		t_detalhe_row_w.descricao	:= c01_w.ds_dano_breve;			
		t_detalhe_row_w.detalhe		:= substr(obter_desc_expressao(738596) || ': ' || campo_mascara_virgula(trunc(dividir(c01_w.qt_voltas,3)) * dividir(c01_w.qt_voltas_filtro, c01_w.qt_voltas)) || ' (' || c01_w.qt_voltas_filtro || '/' || c01_w.qt_voltas || ')',1,255);		

		RETURN NEXT t_detalhe_row_w;		
		end;
	end loop;
	close C01;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
