CREATE OR REPLACE FUNCTION diag_departments_pck.get_diag_departments ( nr_atendimento_p bigint, nr_seq_episodio_p bigint) RETURNS SETOF T_DIAG_DEPART AS $body$
DECLARE

				
r_diag_depart_w				r_diag_depart;		
cd_departamento_w		departamento_medico.cd_departamento%type;
cd_depto_old_w				departamento_medico.cd_departamento%type;
nr_sequencia_w				bigint;
dt_entrada_w					timestamp;
dt_saida_w						timestamp;
nr_atendimento_w			bigint;
nr_seq_episodio_w		bigint;

c01 CURSOR FOR				
SELECT		nr_seq_interno,
				cd_departamento,
				dt_entrada_unidade,
				dt_saida_unidade
from		atend_paciente_unidade a
where		nr_atendimento = nr_atendimento_w
and			nr_seq_episodio_w = 0

union

SELECT		nr_seq_interno,
				cd_departamento,
				dt_entrada_unidade,
				dt_saida_unidade
from		atendimento_paciente b,
				atend_paciente_unidade a
where		b.nr_atendimento = a.nr_atendimento
and			b.nr_seq_episodio = nr_seq_episodio_w
and			nr_seq_episodio_w > 0
order by	nr_seq_interno;
				

BEGIN

nr_atendimento_w		:= coalesce(nr_atendimento_p,0);
nr_seq_episodio_w	:= coalesce(nr_seq_episodio_p,0);

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	cd_departamento_w,
	dt_entrada_w,
	dt_saida_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	if (cd_departamento_w = cd_depto_old_w) then
		r_diag_depart_w.dt_saida					:= dt_saida_w;
		r_diag_depart_w.ds_seq_atepacu		:= r_diag_depart_w.ds_seq_atepacu || nr_sequencia_w || ',';
	else
		RETURN NEXT r_diag_depart_w;
		r_diag_depart_w.ds_seq_atepacu		:= nr_sequencia_w || ',';
		r_diag_depart_w.cd_departamento	:= cd_departamento_w;
		r_diag_depart_w.dt_entrada				:= dt_entrada_w;
		r_diag_depart_w.dt_saida					:= dt_saida_w;	
		cd_depto_old_w									:= cd_departamento_w;		
	end if;
	
	end;
end loop;
close c01;

RETURN NEXT r_diag_depart_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
