CREATE OR REPLACE PROCEDURE dose_medic_md_pck.calcular_dose_infusao_md (ie_tempo_p text, ie_unidade_vel_p text, qt_dose_ml_p INOUT bigint, qt_vel_infusao_p INOUT bigint, ie_primeiro_principal_p text, qt_diluente_p bigint, qt_dose_medic_p INOUT bigint, obter_conversao_ml_p bigint) AS $body$
BEGIN
    if (ie_tempo_p = 'M') or (ie_tempo_p = 'H') then
      begin

        if (upper(ie_unidade_vel_p) = 'MLH') then
          if (ie_tempo_p = 'M') then
            qt_dose_ml_p := coalesce(qt_dose_ml_p, 0) * 60;
          end if;
        end if;


        if (upper(ie_unidade_vel_p) = 'GTM') then
          qt_dose_ml_p := coalesce(qt_dose_ml_p, 0) * 20;
          if (ie_tempo_p = 'H') then
            qt_dose_ml_p := dividir_md(qt_dose_ml_p, 60);
          end if;
        end if;


        if (upper(ie_unidade_vel_p) = 'MGM') then
          qt_dose_ml_p := coalesce(qt_dose_ml_p, 0) * 60;
          if (ie_tempo_p = 'H') then
            qt_dose_ml_p := dividir_md(qt_dose_ml_p, 60);
          end if;
        end if;
        qt_vel_infusao_p := qt_dose_ml_p;
      end;
    else
      qt_vel_infusao_p := qt_dose_ml_p;
    end if;

    if (ie_primeiro_principal_p = 'S') then
      qt_vel_infusao_p := dividir_md(coalesce(qt_dose_ml_p, 0) + coalesce(qt_diluente_p, 0), 24);
      qt_dose_medic_p  := obter_conversao_ml_p;
    else

      qt_dose_medic_p := obter_conversao_ml_p;
    end if;
  end;

$body$
