CREATE OR REPLACE PROCEDURE dose_medic_md_pck.calcular_dose_unidade_md (qt_dose_direta_p INOUT bigint, qt_dose_terap_p INOUT bigint, ie_peso_p text, ie_primeiro_principal_p text, qt_peso_p bigint, qt_dose_ml_p INOUT bigint, qt_concent_dose_total_p bigint) AS $body$
BEGIN
    qt_dose_direta_p := qt_dose_terap_p;

    if (ie_peso_p IS NOT NULL AND ie_peso_p::text <> '') then
      if (ie_primeiro_principal_p = 'N') then
        qt_dose_direta_p := coalesce(qt_peso_p, 0) * coalesce(qt_dose_terap_p, 0);
      else
        qt_dose_direta_p := coalesce(qt_peso_p, 0) * coalesce(qt_dose_terap_p, 0) * 24;
      end if;
    end if;

    qt_dose_ml_p := dividir_sem_round_md(qt_dose_direta_p, qt_concent_dose_total_p);
  end;


$body$
