CREATE OR REPLACE PROCEDURE dose_medic_md_pck.converter_dose_terapeutica_md (ie_unidade_p text, cd_unidade_medida_p INOUT text, cd_uni_cons_p text, qt_dose_terap_p INOUT bigint, qt_dose_terap_w_p INOUT bigint, qt_dose_unid_cons_p INOUT bigint, qt_conversao_p bigint, obter_conversao_meq_p bigint, obter_conversao_ui_p bigint, obter_conversao_gts_p bigint ) AS $body$
BEGIN

    if (ie_unidade_p = 'MG') then
      qt_dose_terap_w_p := qt_dose_terap_p;
    elsif (ie_unidade_p = 'MCG') then
      qt_dose_terap_w_p := dividir_md(qt_dose_terap_p, 1000);
    elsif (ie_unidade_p = 'G') then
      qt_dose_terap_w_p := qt_dose_terap_p * 1000;
    elsif (ie_unidade_p = 'MEQ') then
      qt_dose_unid_cons_p := obter_conversao_meq_p;
    elsif (ie_unidade_p = 'UI') then
      qt_dose_unid_cons_p := obter_conversao_ui_p;
    elsif (ie_unidade_p = 'L') then
      qt_dose_terap_w_p := qt_dose_terap_p * 1000;
    elsif (ie_unidade_p = 'ML') then
      qt_dose_terap_w_p := qt_dose_terap_p;
    elsif (ie_unidade_p = 'UE100') then
      qt_dose_terap_w_p := dividir_md(qt_dose_terap_p, 100);
    elsif (ie_unidade_p = 'GTS') then
      if (upper(cd_unidade_medida_p) <> 'GTS') and (upper(cd_uni_cons_p) = 'GTS') then
        qt_dose_unid_cons_p := dividir_sem_round_md(qt_dose_terap_p, qt_conversao_p);
      else
        qt_dose_unid_cons_p := obter_conversao_gts_p;
      end if;
    elsif (ie_unidade_p = 'MCGT') then
      qt_dose_terap_w_p := dividir_md(qt_dose_terap_p, 50);
    end if;

  end;


$body$
