SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE dpc_pkg.deactivate_stem7 ( nr_seq_edition_p bigint, nm_usuario_p text ) AS $body$
BEGIN

	--stem7--
    UPDATE dpc_stem7
    SET
        ie_situacao = 'I',
        dt_atualizacao_nrec = clock_timestamp(),
        nm_usuario_nrec = nm_usuario_p
    WHERE
        nr_stem7_edition <> nr_seq_edition_p
        AND ie_situacao = 'A';
		
	--stem7points--
	UPDATE dpc_stem7_points
    SET
        ie_situacao = 'I',
        dt_atualizacao_nrec = clock_timestamp(),
        nm_usuario_nrec = nm_usuario_p
    WHERE
        nr_stem7_edition <> nr_seq_edition_p
        AND ie_situacao = 'A';
		
	--stem7edition--
	update DPC_EDITION_STEM7
	set
		ie_situacao = 'I',
        dt_atualizacao_nrec = clock_timestamp(),
        nm_usuario_nrec = nm_usuario_p,
		ds_log = 'Deactivated'
	where nr_sequencia<>nr_seq_edition_p
	and ie_situacao='A';
		
	

    COMMIT;
END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
