SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION dpc_pkg.get_active_edition (dt_start_p text) RETURNS bigint AS $body$
DECLARE


        nr_seq_edition_w    bigint;

BEGIN
        select  max(a.nr_sequencia)
        into STRICT    nr_seq_edition_w
        from    dpc_edition a
                inner join dpc_icd b on a.nr_sequencia = b.nr_seq_edition
        where   coalesce(ie_situacao,'A') = 'A'
        and     (a.dt_import IS NOT NULL AND a.dt_import::text <> '')
        and     (a.dt_procedure_update IS NOT NULL AND a.dt_procedure_update::text <> '')
        and     dt_start_p >= a.dt_start;

        return nr_seq_edition_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
