SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION dpc_pkg.get_disease_name ( cd_sub_icd_p text, nr_disease_number_p text) RETURNS varchar AS $body$
DECLARE

        ds_w varchar(255) := '';

BEGIN

        if (position('-' in cd_sub_icd_p) > 0) then
        begin
                select max(ds)
                into STRICT ds_w
                from (
                        SELECT  b.nr_disease_number nr_dis, b.cd_icd_code ||' - '|| b.ds_disease_name ds
                        from    icd_codes_main_jpn b
                        where   nr_disease_number = nr_disease_number_p
                                and '1' = substr(cd_sub_icd_p,position('-' in cd_sub_icd_p)+1,1)

union   all

                        PERFORM  b.nr_disease_number nr_dis, b.CD_MULTICLASSIF_CODE ||' - '|| b.ds_disease_name ds
                        from    icd_codes_main_jpn b
                        where   nr_disease_number = nr_disease_number_p
                                and '2' = substr(cd_sub_icd_p,position('-' in cd_sub_icd_p)+1,1)
                ) alias7;
        end;
        else
        begin
                select  max(ds)
                into STRICT    ds_w
                from (       SELECT  cd_icd_code ||' - '|| ds_disease_name ds
                        from    icd_codes_main_jpn
                        where   nr_disease_number = nr_disease_number_p
                ) alias1;
        end;
        end if;

        return ds_w;

end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
