CREATE OR REPLACE FUNCTION dpc_pkg.get_surgery_date ( nr_atendimento_p bigint, cd_procedimento_loc_p text) RETURNS timestamp AS $body$
DECLARE


        dt_surgery_w    cirurgia.dt_inicio_real%type;

BEGIN
        select  max(coalesce(a.dt_inicio_real, a.dt_inicio_prevista))
        into STRICT    dt_surgery_w
        from    cirurgia a,
                proc_interno b,
                procedimento c
        where   c.ie_origem_proced = b.ie_origem_proced
        and     c.cd_procedimento = b.cd_procedimento
        and     b.nr_sequencia = a.nr_seq_proc_interno
        and     a.nr_atendimento = nr_atendimento_p
        and     c.CD_PROCEDIMENTO_LOC = cd_procedimento_loc_p;

        return dt_surgery_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
