CREATE OR REPLACE FUNCTION dpc_pkg.get_treatment_detail (nr_seq_dpc_treatment_p bigint, nr_treatment_p bigint, si_option_p text) RETURNS varchar AS $body$
DECLARE


ds_return_w	varchar(255);

BEGIN

if (si_option_p = 'C') then
	if (nr_treatment_p = 1) then
		select  max(cd_treatment_1)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	else
		select  max(cd_treatment_2)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	end if;	
elsif (si_option_p = 'D') then
	if (nr_treatment_p = 1) then
		select  max(ds_treatment_1)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	else
		select  max(ds_treatment_2)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	end if;
elsif (si_option_p = 'CD') then
	if (nr_treatment_p = 1) then
		select  max(cd_treatment_1) ||  ' - ' || max(ds_treatment_1)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	else
		select  max(cd_treatment_2) ||  ' - ' || max(ds_treatment_2)
		into STRICT	ds_return_w
		from    dpc_treatment
		where   nr_sequencia = nr_seq_dpc_treatment_p;
	end if;
end if;

return	ds_return_w;
end;


