CREATE OR REPLACE PROCEDURE dpc_pkg.submit_patient_dpc (NR_SEQ_PATIENT_DPC_P bigint, DT_START_DPC_P timestamp, DT_END_DPC_P timestamp, NM_USUARIO_P text) AS $body$
DECLARE

	ie_gerar_evolucao_dpc_w     varchar(1);
    cd_evolucao_w                   bigint;
    nr_atendimento_w numeric(20);

BEGIN
obter_param_usuario(281, 1629, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1), ie_gerar_evolucao_DPC_w);

CALL dpc_pkg.VALIDATE_PATIENT_DPC(NR_SEQ_PATIENT_DPC_P,DT_START_DPC_P,DT_END_DPC_P);

update PATIENT_DPC set DT_ATUALIZACAO = clock_timestamp(),NM_USUARIO =NM_USUARIO_P, DT_LIBERACAO=clock_timestamp()
WHERE NR_SEQUENCIA = NR_SEQ_PATIENT_DPC_P;

select nr_atendimento
into STRICT nr_atendimento_w
from patient_dpc where nr_sequencia = nr_seq_patient_dpc_p;
if ( ie_gerar_evolucao_DPC_w = 'S' ) then
    begin
        cd_evolucao_w := clinical_notes_pck.gerar_soap(nr_atendimento_w, nr_seq_patient_dpc_p, 'DPC', null, 'P', 1, cd_evolucao_w);
                update patient_dpc
                set
                    cd_evolucao = cd_evolucao_w
                where
                    nr_sequencia = nr_seq_patient_dpc_p;
    end;
end if;

commit;
END;

$body$
