CREATE OR REPLACE FUNCTION dpc_pkg.validate_procedimento_versao ( cd_procedimento_loc_p text, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


        cd_procedimento_w       procedimento.cd_procedimento%type;
        qtd_versao_w            bigint;

BEGIN
        select max(cd_procedimento)
        into STRICT   cd_procedimento_w	
        from   procedimento
        where  cd_procedimento_loc = cd_procedimento_loc_p
        and    ie_origem_proced = ie_origem_proced_p;

        select count(*)
        into STRICT   qtd_versao_w
        from   procedimento_versao
        where  cd_procedimento = cd_procedimento_w
        and    ie_origem_proced = ie_origem_proced_p;

        return qtd_versao_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
