CREATE OR REPLACE FUNCTION encounter_json_pck.get_transf_message_clob ( nr_seq_atepacu_old_p bigint) RETURNS text AS $body$
DECLARE

	json_encounter_w		philips_json;
	json_mov_w			philips_json;
	nr_atendimento_w		bigint;
	ds_json_out_w			text;
	
	
BEGIN
	
	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	atend_paciente_unidade
	where	nr_seq_interno = nr_seq_atepacu_old_p;
	
	json_encounter_w	:= encounter_json_pck.get_encounter_message(nr_atendimento_w);
	json_encounter_w.put('priorPatientLocation', encounter_json_pck.get_movement(nr_seq_atepacu_old_p));
	
	dbms_lob.createtemporary( ds_json_out_w, true);
	json_encounter_w.(ds_json_out_w);
	
	return ds_json_out_w;
	
	END;

$body$
