CREATE OR REPLACE FUNCTION expressao_pck.obter_locale_idioma ( nr_seq_idioma_p bigint) RETURNS varchar AS $body$
BEGIN
	
	case 	nr_seq_idioma_p	
		when 0 then
			return 'pt_BR';
		when 1 then
			return 'pt_BR';
		when 2 then
			return 'es_MX';
		when 3 then
			return 'es_CO';
		when 4 then
		    return 'es_AR';
		when 5 then
			return 'en_US';
		when 7 then
			return 'de_DE';
		when 8 then
			return 'ar_SA';
		when 9 then
			return 'en_AU';
		when 11 then
			return 'pl_PL';
		when 12 then
			return 'ja_JP';
		when 13 then
			return 'es_DO';
		when 14 then
			return 'de_AT';
		when 15 then
			return 'es_BO';
		else
			return 'en_US';
	end case;
			
	return null;
	
	END;

$body$
