CREATE OR REPLACE PROCEDURE fin_capa_ficha_financ_pck.fin_gerar_vl_form_capa ( nr_seq_capa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_macro_w	varchar(50);
ds_formula_w	varchar(4000);
ds_sql_w		varchar(4000);

