vl_resultado_w	double precision;
vl_coluna_w	varchar(17);

c001		integer;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ds_macro,
	a.ds_formula
from	ficha_financ_formula a,
	capa_ficha_financ_val b
where	b.nr_seq_formula = a.nr_sequencia
group by	a.nr_sequencia,
	a.ds_macro,
	a.ds_formula,
	coalesce(a.nr_seq_calculo,999)
order by	coalesce(a.nr_seq_calculo,999),
	a.nr_sequencia;

c02 CURSOR FOR
SELECT	ds_macro,
	vl_resultado
from	w_fin_valor_capa
where	nm_usuario = nm_usuario_p;


BEGIN
open c01;
loop
fetch c01 into
	nr_sequencia_w,
	ds_macro_w,
