CREATE OR REPLACE PROCEDURE fis_refatoracao_nf_pck.calcula_nota_fiscal ( nr_seq_refat_nf_p nota_fiscal.nr_sequencia%type, ie_tipo_nota_p nota_fiscal.ie_tipo_nota%type, nm_usuario_p text) AS $body$
BEGIN

CALL gerar_lote_fornec_nf(nr_seq_refat_nf_p,'N',nm_usuario_p,'N','N');	

if (ie_tipo_nota_p = 'SF') then	
	CALL atualizar_nota_fiscal(nr_seq_refat_nf_p,'I',nm_usuario_p,4);
elsif (ie_tipo_nota_p = 'SE') then
	CALL atualizar_nota_fiscal(nr_seq_refat_nf_p,'I',nm_usuario_p,3);
else
	CALL atualizar_nota_fiscal(nr_seq_refat_nf_p,'I',nm_usuario_p,0);
end if;

commit;

END;

