CREATE OR REPLACE PROCEDURE fis_refatoracao_nf_pck.fis_vinc_nf_cancel_rf ( nr_interno_conta_p nota_fiscal.nr_interno_conta%type, nr_seq_protocolo_p nota_fiscal.nr_seq_protocolo%type, nr_seq_far_venda_p nota_fiscal.nr_seq_far_venda%type, nr_seq_nf_new_p nota_fiscal.nr_sequencia%type) AS $body$
DECLARE


nr_seq_conta_origem_w		conta_paciente.nr_interno_conta%type;
nr_seq_nota_credito_w		nf_credito.nr_sequencia%type;		
		

BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_nota_credito_w
from	nf_credito a
where	a.nr_seq_nf_gerada = nr_seq_nf_new_p;

if	(	(coalesce(nr_interno_conta_p,0) <> 0) or (coalesce(nr_seq_protocolo_p,0) <> 0) or (coalesce(nr_seq_far_venda_p,0) <> 0)) and (coalesce(nr_seq_nota_credito_w, 0) = 0) then
begin
	
	if (coalesce(nr_interno_conta_p,0) <> 0) then
	begin
		select	nr_seq_conta_origem
		into STRICT	nr_seq_conta_origem_w
		from	conta_paciente
		where	nr_interno_conta = nr_interno_conta_p;
		
		update	fis_refatoracao_nf
		set	nr_seq_nf_princ_new = nr_seq_nf_new_p
		where	nr_interno_conta = nr_seq_conta_origem_w
		and 	nr_seq_nf_princ_old <> nr_seq_nf_new_p;
	
	end;
	end if;
	if (coalesce(nr_seq_protocolo_p,0) <> 0) then
	begin
	
		update	fis_refatoracao_nf
		set	nr_seq_nf_princ_new = nr_seq_nf_new_p
		where	nr_seq_protocolo = nr_seq_protocolo_p
		and 	nr_seq_nf_princ_old <> nr_seq_nf_new_p;
	
	end;
	end if;
	if (coalesce(nr_seq_far_venda_p,0) <> 0) then
	begin
		update	fis_refatoracao_nf
		set	nr_seq_nf_princ_new = nr_seq_nf_new_p
		where	nr_seq_far_venda = nr_seq_far_venda_p
		and 	nr_seq_nf_princ_old <> nr_seq_nf_new_p;
	end;
	end if;
	


end;	
end if;
END;

$body$
