/*Procedure de graçâo de dados dos registros*/

CREATE OR REPLACE PROCEDURE fis_sef_edoc_reg_lfpd05_pck.fis_gerar_dados_edoc_icms ( nr_seq_controle_p bigint) AS $body$
DECLARE


nr_seq_regra_w	fis_sef_edoc_regra.nr_sequencia%type;
cd_reg_w	fis_sef_edoc_regra.cd_reg%type;

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		upper(b.cd_reg)
	from	fis_sef_edoc_controle a,
		fis_sef_edoc_regra b
	where	a.nr_sequencia		= b.nr_seq_controle
	and	a.nr_sequencia		= nr_seq_controle_p;


BEGIN

CALL CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_dados_usuario_edoc();

CALL fis_sef_edoc_reg_lfpd05_pck.fis_limpa_dados_edoc_icms(nr_seq_controle_p);

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	cd_reg_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	fis_sef_edoc_controle
	set	nr_cod_0450 = current_setting('fis_sef_edoc_reg_lfpd05_pck.nr_cod_0450_w')::fis_sef_edoc_controle.nr_cod_0450%type
	where	nr_sequencia = nr_seq_controle_p;

	CALL CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_dados_controle_edoc(	nr_seq_controle_p,
					nr_seq_regra_w);

	if	((cd_reg_w = 'E020') or (coalesce(cd_reg_w, 'X') = 'X')) then
		CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_reg_e020_edoc();
	end if;

	if	((cd_reg_w = 'E100') or (coalesce(cd_reg_w, 'X') = 'X')) then
		CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_reg_e100_edoc();
	end if;

	if	((cd_reg_w = 'E120') or (coalesce(cd_reg_w, 'X') = 'X')) then
		CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_reg_e120_edoc();
	end if;

	end;
end loop;
close C01;

CALL fis_sef_edoc_reg_lfpd05_pck.fis_gerar_reg_e003_edoc();


update fis_sef_edoc_controle
set ie_mov_e 		= 'S'
where nr_sequencia 	= nr_seq_controle_p;

END;

$body$
