CREATE OR REPLACE PROCEDURE generate_data_hcp_pkg.generate_dataset_hcp_seg (nr_seq_batch_p bigint, nr_atendimento_p bigint, nm_usuario_p text, nr_seq_arquivo_p bigint, ie_dataset_type text, cd_convenio_p text, returned_value_p INOUT bigint, other_exception_p INOUT text) AS $body$
DECLARE

	 nr_seq_dataset_w             hcp_dataset_send.nr_sequencia%TYPE;
	 is_insertion_successful      bigint := 0;
	 excp_caught_in_dataset_ins   varchar(500 );
	 is_clob_insertion_successful bigint := 0;
	 excp_caught_dataset_clob_ins varchar(500 );
	
BEGIN
		IF ( ie_dataset_type = 'HCP' or ie_dataset_type = 'PHDB' ) THEN 
			SELECT * FROM generate_data_hcp_pkg.insert_dataset(nr_seq_batch_p, nr_seq_arquivo_p, ie_dataset_type, nm_usuario_p, nr_atendimento_p, nr_seq_dataset_w, is_insertion_successful, excp_caught_in_dataset_ins) INTO STRICT nr_seq_dataset_w, is_insertion_successful, excp_caught_in_dataset_ins;

			Generate_hpc_data_content(nr_seq_dataset_w, nm_usuario_p, cd_convenio_p, is_clob_insertion_successful, excp_caught_dataset_clob_ins);
		ELSIF ( ie_dataset_type = 'SNAP' )THEN 
			SELECT * FROM generate_data_hcp_pkg.insert_dataset(nr_seq_batch_p, nr_seq_arquivo_p, 'SNAP', nm_usuario_p, nr_atendimento_p, nr_seq_dataset_w, is_insertion_successful, excp_caught_in_dataset_ins) INTO STRICT nr_seq_dataset_w, is_insertion_successful, excp_caught_in_dataset_ins;

			Generate_hpc_snap_content(nr_seq_dataset_w, nm_usuario_p, cd_convenio_p,is_clob_insertion_successful, excp_caught_dataset_clob_ins);
		END IF;

		IF ( is_insertion_successful = 1 AND is_clob_insertion_successful = 1 ) THEN 
			returned_value_p := 1;

			other_exception_p := NULL;
		ELSE 
			IF ( (excp_caught_in_dataset_ins IS NOT NULL AND excp_caught_in_dataset_ins::text <> '') OR (excp_caught_dataset_clob_ins IS NOT NULL AND excp_caught_dataset_clob_ins::text <> '') ) THEN 
				returned_value_p := 0;

				other_exception_p := excp_caught_in_dataset_ins ||Chr(13) ||Chr(10) ||excp_caught_dataset_clob_ins;
			END IF;
		END IF;

		NULL;
	END;

$body$
