SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ger_carga_pck.importar_cid_categoria_gm ( nr_seq_carga_p ger_carga_inicial.nr_sequencia%type, nr_seq_carga_arq_p ger_carga_arq.nr_sequencia%type, nr_sequencia_p bigint) AS $body$
DECLARE

	qt_cartegoria_w bigint;

	c_cid_categoria_gm CURSOR FOR
	SELECT  nr_seq_carga_arq,
		nr_linha,
		nr_sequencia,
		cd_categoria_inicial,
		cd_categoria_final,
		cd_especialidade,
		substr(ds_categoria, 1, 240) ds_categoria
	from    cid_categoria_gm
	where   ie_status	= 'V'
	and     nr_seq_carga = nr_seq_carga_p
	and     nr_seq_carga_arq = coalesce(nr_seq_carga_arq_p,nr_seq_carga_arq)
	and     nr_sequencia = coalesce(nr_sequencia_p, nr_sequencia);

	
BEGIN

	for 	row_cid_categoria_gm in c_cid_categoria_gm loop

		reg_proc_w := ger_carga_pck.set_carga_arq(reg_proc_w, row_cid_categoria_gm.nr_seq_carga_arq, row_cid_categoria_gm.nr_linha, row_cid_categoria_gm.nr_sequencia, 'S');
		CALL ger_carga_pck.atualizar_processamento( 'CID_CATEGORIA_GM', row_cid_categoria_gm.nr_sequencia);
		PERFORM set_config('ger_carga_pck.ie_update_w', null, false);

		begin
		select count(*)
		into STRICT   qt_cartegoria_w
		from   cid_categoria
		where  cd_categoria_cid = row_cid_categoria_gm.cd_categoria_inicial;

		if (qt_cartegoria_w = 0) and (ie_tipo_proc_p = 'IMP') then
			begin
			insert into cid_categoria(cd_categoria_cid,
				ds_categoria_cid,
				dt_atualizacao,
				nm_usuario,
				cd_especialidade,
				ds_categoria_compl,
				ds_referencia,
				ds_excluidos,
				ie_situacao,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (row_cid_categoria_gm.cd_categoria_inicial, -- cd_categoria_cid
				row_cid_categoria_gm.ds_categoria, -- ds_categoria_cid
				clock_timestamp(), -- dt_atualizacao
				nm_usuario_w, -- nm_usuario
				row_cid_categoria_gm.cd_especialidade, -- cd_especialidade
				null, -- ds_categoria_compl
				null, -- ds_referencia
				null,
				'A', -- ie_situacao
				clock_timestamp(), -- dt_atualizacao_nrec
				nm_usuario_w); -- nm_usuario_nrec
			end;
		elsif (current_setting('ger_carga_pck.ie_atualizar_w')::varchar(1) = 'S') and (ie_tipo_proc_p = 'IMP') then
			begin
			update  cid_categoria
			set     ds_categoria_cid    = row_cid_categoria_gm.ds_categoria,
				dt_atualizacao      = clock_timestamp(),
				nm_usuario          = nm_usuario_w,
				cd_especialidade    = row_cid_categoria_gm.cd_especialidade,
				ie_situacao         = 'A'
			where  	CD_CATEGORIA_CID     = row_cid_categoria_gm.cd_categoria_inicial;
			PERFORM set_config('ger_carga_pck.ie_update_w', 'S;', false);
			end;
		end if;

		if (reg_proc_w.qt_reg_log > 0) then
			begin
			/*'Em caso de qualquer consistencia o sistema efetua rollback, atualiza o status para Erro e registra todos os logs de consistencia'*/

			rollback;
			update	cid_categoria_gm
			set	ie_status 	= 'E',
				dt_fim_proc	= clock_timestamp()
			where	nr_sequencia 	= row_cid_categoria_gm.nr_sequencia;
			end;
		elsif (ie_tipo_proc_p = 'IMP') then
			update	cid_categoria_gm
			set	ie_status 	= 'I',
				ds_chave_tasy	= current_setting('ger_carga_pck.ie_update_w')::varchar(2)||row_cid_categoria_gm.cd_categoria_inicial,
				dt_fim_proc	= clock_timestamp()
			where	nr_sequencia 	= row_cid_categoria_gm.nr_sequencia;
		else
			update	cid_categoria_gm
			set	ie_status 	= 'V',
				dt_fim_proc	= clock_timestamp()
			where	nr_sequencia 	= row_cid_categoria_gm.nr_sequencia;
		end if;

		exception
		when others then
			begin
			ds_erro_w := substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1,4000);

			rollback;

			reg_proc_w := ger_carga_pck.incluir_ger_carga_log_import(reg_proc_w, '4', ds_erro_w);

			update	cid_categoria_gm
			set	ie_status 	= 'E',
				dt_fim_proc	= clock_timestamp()
			where	nr_sequencia 	= row_cid_categoria_gm.nr_sequencia;

			end;
		end;

		commit;
	end loop;

	end;
--==============================================================================
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ger_carga_pck.importar_cid_categoria_gm ( nr_seq_carga_p ger_carga_inicial.nr_sequencia%type, nr_seq_carga_arq_p ger_carga_arq.nr_sequencia%type, nr_sequencia_p bigint) FROM PUBLIC;
