CREATE OR REPLACE FUNCTION ger_pres_mat_s_dt_lib_md_pck.obter_disp_hor_nut_md ( ie_gerar_npt_fases_p text, ds_dose_diferenciada_p text, ie_dose_diferenciada_existe_p text, qt_dose_diferenciada_p bigint, qt_total_dispensar_p bigint, nr_ocorrencia_p bigint) RETURNS bigint AS $body$
DECLARE

	qt_disp_hor_nut_w bigint;
	
BEGIN
		if (coalesce(ie_gerar_npt_fases_p,'N') = 'S') then
			if (ds_dose_diferenciada_p IS NOT NULL AND ds_dose_diferenciada_p::text <> '') then
				if (ie_dose_diferenciada_existe_p = 'S') and (qt_dose_diferenciada_p IS NOT NULL AND qt_dose_diferenciada_p::text <> '') and (qt_dose_diferenciada_p > 0) then
					qt_disp_hor_nut_w	:= coalesce(qt_dose_diferenciada_p,0);
				else
					qt_disp_hor_nut_w	:= 0;
				end if;
			else
				qt_disp_hor_nut_w	:= 0;
			end if;
		else
			qt_disp_hor_nut_w	:= dividir_md(qt_total_dispensar_p,nr_ocorrencia_p);
		end if;

		return coalesce(qt_disp_hor_nut_w,0);
	end;


