CREATE OR REPLACE PROCEDURE ger_pres_mat_s_dt_lib_md_pck.obter_dose_esp_dispen_md ( ie_mat_solucao_p text, ie_urgente_p text, ie_funcao_origem_p text, ds_hora_p text, ds_horarios_padr_p text, qt_k_p bigint, qt_dia_adic_p INOUT bigint, ie_urgente_ant_p INOUT text, ie_etapa_especial_2_p INOUT text) AS $body$
BEGIN
		if (coalesce(ie_mat_solucao_p,'N') <> 'S') then
			if	((ie_urgente_p = 'N') or (ie_funcao_origem_p = 'S')) then
				if (position('A' in ds_hora_p) > 0) and (qt_dia_adic_p = 0) then
					qt_dia_adic_p	:= 1;
				elsif (position('AA' in ds_hora_p) > 0) and (ie_urgente_p = 'N') then
					qt_dia_adic_p	:= coalesce(qt_dia_adic_p,0) + 1;
				end if;
			end if;
		else
		  if	((ie_urgente_ant_p = 'N') or (ie_funcao_origem_p = 'S')) then
			if (position('A' in ds_hora_p) > 0) and (qt_dia_adic_p = 0) then
				qt_dia_adic_p	:= 1;
			elsif (position('AA' in ds_hora_p) > 0) then
				qt_dia_adic_p	:= coalesce(qt_dia_adic_p,0) + 1;
			end if;
		  end if;

		  if (ie_etapa_especial_2_p = 'S') or (position('AA' in substr(ds_horarios_padr_p, 1, qt_k_p-1)) > 0) then
			ie_urgente_ant_p	 := 'N';
			ie_etapa_especial_2_p := 'N';
		  end if;
		end if;
	end;


$body$
