CREATE OR REPLACE FUNCTION ger_pres_mat_s_dt_lib_md_pck.obter_dt_horario_md (dt_horario_p timestamp, dt_prescricao_p timestamp, ie_urgente_p text, ie_agora_especial_p text, ie_dose_espec_agora_p text, ie_operacao_interv_p text, ds_horarios_inter_p text, nr_ocorrencia_p bigint, ds_hor_valido_inter1_p text, ds_hor_valido_inter2_p text, dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp, nr_horas_validade_p bigint, dt_prescricao_aux_p timestamp ) RETURNS timestamp AS $body$
DECLARE


      dt_horario_w timestamp;

BEGIN
    dt_horario_w := dt_horario_p;

    if (dt_horario_p < dt_prescricao_p) and
      ((ie_urgente_p = 'N') or
        (ie_agora_especial_p	= 'S' AND ie_dose_espec_agora_p	= 'S')) then
      dt_horario_w	:= dt_horario_p + 1;
    end if;

    if (dt_horario_p < dt_prescricao_aux_p) and (ie_urgente_p = 'S') and (pkg_date_utils.extract_field('HOUR', dt_prescricao_aux_p, 0) in (21, 22, 23)) then
      dt_horario_w	:= dt_horario_p + 1;
    end if;

    if (ie_operacao_interv_p = 'D') and (coalesce(ds_horarios_inter_p::text, '') = '') and (coalesce(nr_ocorrencia_p,0) = 1) and (ds_hor_valido_inter1_p = 'I') and (ds_hor_valido_inter2_p = 'A') and (dt_horario_p + 1 between dt_inicio_prescr_p and dt_validade_prescr_p) and (nr_horas_validade_p > 24) then

        dt_horario_w := dt_horario_p + 1;
    end if;
    --- Fim MD10
    return dt_horario_w;
  end;


$body$
