CREATE OR REPLACE PROCEDURE gerar_dados_301_pck.inserir_dataset ( nr_seq_lote_p bigint, nr_seq_arquivo_p bigint, nr_seq_log_scheduler_p bigint, ie_dataset_p text, nm_usuario_p text, nr_referencia_p bigint, nr_seq_dataset_p INOUT bigint) AS $body$
DECLARE


	nr_seq_dataset_w		d301_dataset_envio.nr_sequencia%type;
	nr_atendimento_w		atendimento_paciente.nr_atendimento%type	:= null;
	nr_interno_conta_w		conta_paciente.nr_interno_conta%type		:= null;
	nr_seq_nota_fiscal_w		nota_fiscal.nr_sequencia%type			:= null;
	nr_seq_atend_prev_alta_w	atend_previsao_alta.nr_sequencia%type		:= null;
	nr_seq_atend_alta_w		atendimento_alta.nr_sequencia%type		:= null;
	nr_seq_import_301_inka_w	i301_arquivo_inka.nr_sequencia%type		:= null;

	nr_seq_arquivo_w		d301_arquivo_envio.nr_sequencia%type;
	cd_convenio_w			convenio.cd_convenio%type	:= null;

	
BEGIN

		select	nextval('d301_dataset_envio_seq')
		into STRICT	nr_seq_dataset_w
		;

		if (ie_dataset_p in ('AUFN','PAUF','ENTL','PENT')) then
			nr_atendimento_w		:= nr_referencia_p;
		elsif (ie_dataset_p in ('VERL','MBEG','PVER')) then
			nr_seq_atend_prev_alta_w	:= nr_referencia_p;
			nr_atendimento_w		:= gerar_dados_301_pck.obter_atendimento(nr_referencia_p,ie_dataset_p);
		/*elsif	(ie_dataset_p in ('ENTL')) then
			nr_seq_atend_alta_w		:= nr_referencia_p;*/
		elsif (ie_dataset_p in ('ZGUT')) then
			nr_seq_nota_fiscal_w		:= nr_referencia_p;
			nr_atendimento_w		:= obter_nr_atendimento_nota(nr_seq_nota_fiscal_w);
		elsif (ie_dataset_p in ('INKA')) then
			nr_seq_import_301_inka_w	:= nr_referencia_p;
		elsif (ie_dataset_p in ('RECH','AMBO','PREC')) then
			nr_interno_conta_w		:= nr_referencia_p;
			nr_atendimento_w		:= gerar_dados_301_pck.obter_atendimento(nr_referencia_p,ie_dataset_p);
		end if;

		insert into d301_dataset_envio(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_arquivo,
			ie_dataset,
			nr_ordem,
			nr_atendimento,
			nr_interno_conta,
			nr_seq_nota_fiscal,
			nr_seq_atend_prev_alta,
			nr_seq_atend_alta,
			nr_seq_import_301_inka)
		values (nr_seq_dataset_w,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_arquivo_p,
			ie_dataset_p,
			nextval('dataset_301_ordem_seq'),
			nr_atendimento_w,
			nr_interno_conta_w,
			nr_seq_nota_fiscal_w,
			nr_seq_atend_prev_alta_w,
			nr_seq_atend_alta_w,
			nr_seq_import_301_inka_w);

		nr_seq_dataset_p	:= nr_seq_dataset_w;

	END;

$body$
