SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION gerar_dados_301_pck.obter_atendimento ( nr_referencia_p bigint, ie_dataset_p text) RETURNS bigint AS $body$
DECLARE


	nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
	
BEGIN

	if (ie_dataset_p in ('VERL','PVER')) then
		select	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	atend_previsao_alta
		where	nr_sequencia	= nr_referencia_p;
	elsif (ie_dataset_p = 'MBEG') then
		select	max(a.nr_atendimento)
		into STRICT	nr_atendimento_w
		from	atend_previsao_alta a,
			atend_prev_alta_just b
		where	b.nr_sequencia	= nr_referencia_p
		and	b.nr_seq_atend_prev_alta = a.nr_sequencia;
	elsif (ie_dataset_p in ('RECH','AMBO','PREC')) then
		select	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	conta_paciente
		where	nr_interno_conta	= nr_referencia_p;
	end if;

	return	nr_atendimento_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
