CREATE OR REPLACE PROCEDURE gerar_dados_302_pck.obter_seq_arquivo (nr_seq_lote_p bigint, cd_convenio_p bigint, nm_usuario_p text, nr_seq_arquivo_p INOUT bigint) AS $body$
DECLARE


	nr_seq_arquivo_w	d302_arquivo_envio.nr_sequencia%type;
	nr_seq_lote_w		d302_lote_envio.nr_sequencia%type;
	cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
	ds_nome_arquivo_w	d302_arquivo_envio.ds_nome_arquivo%type;

	
BEGIN

	select	max(a.nr_sequencia) nr_seq_arquivo
	into STRICT	nr_seq_arquivo_w
	from	d301_arquivo_envio a
	where	a.nr_seq_lote_envio = nr_seq_lote_p
	and	a.cd_convenio	= cd_convenio_p;

	if (coalesce(nr_seq_arquivo_w::text, '') = '') then
		if (coalesce(nr_seq_lote_p::text, '') = '') then
			select	nextval('d302_lote_envio_seq')
			into STRICT	nr_seq_lote_w
			;

			select 	wheb_usuario_pck.get_cd_estabelecimento
			into STRICT	cd_estabelecimento_w
			;

			insert into d302_lote_envio(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				cd_estabelecimento,
				dt_inicio_geracao)
			values (nr_seq_lote_p,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				cd_estabelecimento_w,
				trunc(clock_timestamp(),'dd'));
		else
			select	cd_estabelecimento
			into STRICT	cd_estabelecimento_w
			from	d301_lote_envio a
			where	a.nr_sequencia = nr_seq_lote_p;

			nr_seq_lote_w	:= nr_seq_lote_p;
		end if;

		select	nextval('d302_arquivo_envio_seq')
		into STRICT	nr_seq_arquivo_w
		;

		insert into d302_arquivo_envio(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_lote_envio,
			ds_nome_arquivo,
			cd_convenio)
		values (nr_seq_arquivo_w,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_lote_w,
			'File_'||nr_seq_arquivo_w,
			cd_convenio_p);

		CALL ajustar_newvalue_sequence('DATASET_302_ORDEM_SEQ',0);
	end if;

	nr_seq_arquivo_p	:= nr_seq_arquivo_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
