CREATE OR REPLACE PROCEDURE gerar_esocial_dados_pck.carregar_esocial_conv_ext_dep () AS $body$
DECLARE

	
	c01 CURSOR FOR
		SELECT 	nm_atributo,
			cd_externo
		from 	regra_conv_meio_ext a,
			conversao_meio_externo b
		where	a.nr_sequencia			= b.nr_seq_regra
		and	a.ie_situacao			= 'A'
		and	upper(b.ie_sistema_externo)	= 'ESOCIAL'
		and	upper(b.nm_tabela)		= 'ESOCIAL_DADOS_DEPENDENTE';
	
	c01_w		c01%rowtype;
	
BEGIN
	open c01;
	loop
	fetch c01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		  if (c01_w.nm_atributo = 'IE_AVISO_VACINA') then
			PERFORM set_config('gerar_esocial_dados_pck.ie_aviso_vacina_w', c01_w.cd_externo, false);
		  end if;
		end;
	end loop;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
