CREATE OR REPLACE PROCEDURE gerar_esocial_dados_pck.carregar_esocial_conv_ext_pag () AS $body$
DECLARE


	c01 CURSOR FOR
		SELECT	nm_atributo,
			cd_externo
		from	conversao_meio_externo
		where	upper(ie_sistema_externo) 	= 'ESOCIAL'
		and 	upper(nm_tabela) 		= 'ESOCIAL_DADOS_PAGAMENTO';

	c01_w		c01%rowtype;

	
BEGIN

	open c01;
	loop
	fetch c01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (c01_w.nm_atributo = 'CD_FILIAL_COLAB') then
			PERFORM set_config('gerar_esocial_dados_pck.cd_filial_colab_pag_w', c01_w.cd_externo, false);
		end if;

		end;
	end loop;
	close c01;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
