CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_cancelamento_lote ( nr_prescricao_p bigint, nr_seq_lote_p bigint, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
