CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_cancelamento_lote_atx ( nr_prescricao_p bigint, nr_seq_lote_p bigint, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


	nr_sequencia_w			prescr_mat_hor.nr_sequencia%type;
	cd_material_w			prescr_mat_hor.cd_material%type;
	nr_seq_lote_w			prescr_mat_hor.nr_seq_lote%type;
	qt_dispensar_w			prescr_mat_hor.qt_dispensar%type;
	ie_local_new_dankia_w	varchar(1);C01 CURSOR FOR
		SELECT	a.nr_sequencia,
			a.cd_material,
			a.nr_seq_lote,
			coalesce(a.qt_dispensar_hor,0)
		from	prescr_mat_hor a,
			prescr_medica b
		where	a.nr_prescricao = b.nr_prescricao
		and	b.nr_atendimento = nr_atendimento_w
		and	a.nr_prescricao = nr_prescricao_p
		and	a.nr_seq_lote = nr_seq_lote_p
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	(a.nr_seq_lote IS NOT NULL AND a.nr_seq_lote::text <> '');
		
	
BEGIN
	
	if (get_ie_int_dankia = 'S') then
	
		select	coalesce(max('S'), 'N')
		into STRICT	ie_local_new_dankia_w
		from	local_estoque c,
			dis_regra_setor b,
			dis_regra_local_setor a
		where	a.nr_seq_dis_regra_setor = b.nr_sequencia
		and	c.cd_local_estoque = a.cd_local_estoque		
		and	a.cd_local_estoque = cd_local_estoque_p
		and	c.ie_tipo_local = '11';	
		
		select	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
		
		if (ie_local_new_dankia_w = 'S') then
			open C01;
			loop
			fetch C01 into	
				nr_sequencia_w,
				cd_material_w,
				nr_seq_lote_w,
				qt_dispensar_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				CALL gerar_int_dankia_pck.atualizar_prescr_mat_hor(	nr_sequencia_w,
										cd_material_w,
										nr_seq_lote_w,
										qt_dispensar_w,
										'E',
										cd_local_estoque_p,
										nm_usuario_p);

				end;
			end loop;
			close C01;
		end if;
	end if;
	commit;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
