CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_disp_atualiza_paciente (cd_estabelecimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, ie_sexo_p text, dt_nascimento_p timestamp) AS $body$
BEGIN
        	if (get_ie_int_dankia = 'S') then

        		select 	max(b.nm_contato)
        		into STRICT	current_setting('gerar_int_dankia_pck.nm_mae_w')::compl_pessoa_fisica.nm_contato%type
        		from	compl_pessoa_fisica b
        		where	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
				and 	b.ie_tipo_complemento = 5;

        		select count(1)
        		into STRICT current_setting('gerar_int_dankia_pck.qt_existe_w')::bigint
        		from dankia_disp_paciente d
        		where d.cd_pessoa_fisica = cd_pessoa_fisica_p;

        		if (current_setting('gerar_int_dankia_pck.qt_existe_w')::bigint > 0) then

        			insert into dankia_disp_paciente(
        					nr_sequencia,
        					cd_estabelecimento,
        					dt_atualizacao,
        					nm_usuario,
        					cd_pessoa_fisica,
        					nm_paciente,
        					nm_mae,
        					ie_sexo,
        					dt_nascimento,
        					ie_operacao,
        					dt_lido_dankia,
        					ie_processado,
        					ds_processado_observacao,
        					ds_stack)
        			values (	nextval('dankia_disp_paciente_seq'),
        					cd_estabelecimento_p,
        					clock_timestamp(),
        					nm_usuario_p,
        					cd_pessoa_fisica_p,
        					nm_pessoa_fisica_p,
        					current_setting('gerar_int_dankia_pck.nm_mae_w')::compl_pessoa_fisica.nm_contato%type,
        					ie_sexo_p,
        					dt_nascimento_P,
        					'A',
        					null,
        					'N',
        					null,
        					substr(dbms_utility.format_call_stack,1,2000));
        		end if;
        	end if;
        end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
