CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_disp_material ( cd_material_p bigint, cd_local_estoque_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
BEGIN
	
	if (get_ie_int_dankia = 'S') and (gerar_int_dankia_pck.get_ie_local_dankia(cd_local_estoque_p) = 'S') then
		
		select	max(cd_material_generico),
				max(ie_situacao),
				max(ds_material),
				max(cd_unidade_medida_consumo),
				max(ie_tipo_material)		
		into STRICT 	current_setting('gerar_int_dankia_pck.cd_material_generico_w')::material.cd_material_generico%type,
				current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type,
				current_setting('gerar_int_dankia_pck.ds_material_w')::material.ds_material%type,
				current_setting('gerar_int_dankia_pck.cd_unidade_medida_w')::material.cd_unidade_medida_consumo%type,
				current_setting('gerar_int_dankia_pck.ie_tipo_material_w')::material.ie_tipo_material%type
		from 	material
		where 	cd_material = cd_material_p;
		
		insert into gerar_int_dankia_pck.dankia_disp_material(
				nr_sequencia,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_lido_dankia,
				cd_material,
				cd_material_generico,
				ie_situacao,
				ds_material,
				ds_unidade_medida,
				cd_unidade_medida,
				ie_tipo_material,
				ie_operacao,
				ie_processado,
				ie_controlado,
				ds_processado_observacao,
				ds_stack)
		values (	nextval('dankia_disp_material_seq'),
				gerar_int_dankia_pck.get_cd_estabelecimento(cd_local_estoque_p),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				null,
				cd_material_p,
				coalesce(current_setting('gerar_int_dankia_pck.cd_material_generico_w')::material.cd_material_generico%type,cd_material_p),
				current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type,
				substr(current_setting('gerar_int_dankia_pck.ds_material_w')::material.ds_material%type,1,255),
				substr(obter_desc_unidade_medida(current_setting('gerar_int_dankia_pck.cd_unidade_medida_w')::material.cd_unidade_medida_consumo%type),1,40),
				current_setting('gerar_int_dankia_pck.cd_unidade_medida_w')::material.cd_unidade_medida_consumo%type,
				current_setting('gerar_int_dankia_pck.ie_tipo_material_w')::material.ie_tipo_material%type,
				ie_operacao_p,
				'N',
				substr(obter_se_medic_controlado(cd_material_p),1,1),
				null,
				substr(dbms_utility.format_call_stack,1,2000));
	end if;
	end;
	

