CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_disp_padrao_local ( cd_material_p bigint, cd_local_estoque_p bigint, qt_minimo_p bigint, qt_maximo_p bigint, ie_operacao_p text, nr_seq_estrut_mat_p bigint, nm_usuario_p text) AS $body$
BEGIN
	
	if (get_ie_int_dankia = 'S') and (gerar_int_dankia_pck.get_ie_local_dankia(cd_local_estoque_p) = 'S') then
	
		select 	max(coalesce(ie_situacao,'I'))
		into STRICT	current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type
		from	material
		where 	cd_material = cd_material_p;
		
		if (current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type = 'A') then
			select	coalesce(max(ie_estoque_lote),'N')
			into STRICT	current_setting('gerar_int_dankia_pck.sn_lote_validade_w')::material_estab.ie_estoque_lote%type
			from 	material_estab
			where 	cd_material = cd_material_p
			and     cd_estabelecimento = gerar_int_dankia_pck.get_cd_estabelecimento(cd_local_estoque_p);
			
			
			insert into gerar_int_dankia_pck.dankia_disp_padrao_local(
						nr_sequencia,
						cd_estabelecimento,
						dt_atualizacao,
						nm_usuario,
						cd_material,
						cd_local_estoque,
						qt_minimo,
						qt_maximo,
						sn_lote_validade,
						ds_localizacao,
						dt_lido_dankia,
						ie_operacao,
						ie_processado,
						ds_processado_observacao,
						ds_stack)
				values (nextval('dankia_disp_padrao_local_seq'),
						gerar_int_dankia_pck.get_cd_estabelecimento(cd_local_estoque_p),
						clock_timestamp(),
						nm_usuario_p,
						cd_material_p,
						cd_local_estoque_p,
						qt_minimo_p,
						qt_maximo_p,
						current_setting('gerar_int_dankia_pck.sn_lote_validade_w')::material_estab.ie_estoque_lote%type,
						substr(obter_desc_estrut_loc_material(nr_seq_estrut_mat_p),1,255),
						null,
						ie_operacao_p,
						'N',
						null,
						substr(dbms_utility.format_call_stack,1,2000));			
		end if;
	end if;
	end;
	

$body$
