CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_disp_saldo_local ( cd_material_p bigint, cd_barras_p text, qt_material_p bigint, cd_local_estoque_p bigint, cd_operacao_estoque_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN
		if (get_ie_int_dankia = 'S') then
			select 	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_saldo_disp_w
			from 	dankia_disp_saldo_local
			where 	cd_barras = cd_barras_p
			and 	cd_local_estoque = cd_local_estoque_p
			and 	cd_estabelecimento = cd_estabelecimento_p;
			if (nr_seq_saldo_disp_w > 0) then
				update	dankia_disp_saldo_local
				set		qt_saldo = (qt_saldo + qt_material_p)
				where	nr_sequencia = nr_seq_saldo_disp_w;
			else
				insert into gerar_int_dankia_pck.dankia_disp_saldo_local(nr_sequencia,
						cd_estabelecimento,
						dt_atualizacao,
						nm_usuario,
						cd_material,
						cd_barras,
						cd_local_estoque,
						qt_saldo,
						vl_custo,
						cd_operacao_estoque,
						nr_seq_dankia,
						dt_lido_dankia,
						ie_processado,
						ds_processado_observacao,
						ds_stack)
				values (nextval('dankia_disp_saldo_local_seq'),
						cd_estabelecimento_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_material_p,
						cd_barras_p,
						cd_local_estoque_p,
						qt_material_p,
						null,
						cd_operacao_estoque_p,
						null,
						null,
						'N',
						null,
						substr(dbms_utility.format_call_stack,1,2000));
			end if;
		end if;
		end;
		

$body$
