CREATE OR REPLACE PROCEDURE gerar_int_dankia_pck.dankia_inserir_funcionario ( nm_usuario_p text, ie_operacao_p text) AS $body$
BEGIN
		if (get_ie_int_dankia = 'S') then
			select	max(ie_profissional),
				max(ds_usuario),
				max(cd_barras),
				max(ie_situacao),
				max(cd_estabelecimento)
			into STRICT	current_setting('gerar_int_dankia_pck.ie_profissional_w')::usuario.ie_profissional%type,
				current_setting('gerar_int_dankia_pck.ds_usuario_w')::usuario.ds_usuario%type,
				current_setting('gerar_int_dankia_pck.cd_barras_w')::usuario.cd_barras%type,
				current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type,
				cd_estab_w
			from	usuario
			where	nm_usuario = nm_usuario_p;
				
			insert into dankia_disp_funcionario(
					nr_sequencia,
					ie_profissional,
					dt_atualizacao,
					nm_usuario,
					nm_funcionario,
					cd_barras,
					ie_situacao,
					ie_operacao,
					cd_estabelecimento,
					dt_lido_dankia,
					ie_processado,
					ds_processado_observacao,
					ds_stack)
			values (nextval('dankia_disp_funcionario_seq'),
					current_setting('gerar_int_dankia_pck.ie_profissional_w')::usuario.ie_profissional%type,
					clock_timestamp(),
					nm_usuario_p,
					current_setting('gerar_int_dankia_pck.ds_usuario_w')::usuario.ds_usuario%type,
					current_setting('gerar_int_dankia_pck.cd_barras_w')::usuario.cd_barras%type,
					current_setting('gerar_int_dankia_pck.ie_situacao_w')::usuario.ie_situacao%type,
					ie_operacao_p,
					coalesce(wheb_usuario_pck.get_cd_estabelecimento,cd_estab_w),
					null,
					'N',
					null,
					substr(dbms_utility.format_call_stack,1,2000));
		end if;
		end;
	

$body$
