CREATE OR REPLACE PROCEDURE gerar_pacote_autorizacao_pck.consiste_se_gera_pacote () AS $body$
DECLARE


ie_interno_w	estagio_autorizacao.ie_interno%type;


BEGIN

select	a.ie_interno
into STRICT	ie_interno_w
from	autorizacao_convenio b,
	estagio_autorizacao a
where	a.nr_sequencia	= b.nr_seq_estagio
and	b.nr_sequencia	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;

if (ie_interno_w <> '1') then

	--Esta autorização não está em estágio de necessidade.
	--Para geração dos pacotes, é necessário que o estágio desta autorização seja equivalente ao estágio de necessidade.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(346792);

end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
