CREATE OR REPLACE PROCEDURE gerar_pacote_autorizacao_pck.descobrar_proc_autorizado (nr_seq_proc_autor_p bigint, qt_solicidata_p bigint, nr_seq_pacote_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	nextval('procedimento_autorizado_seq')
into STRICT	nr_sequencia_w
;

insert into procedimento_autorizado(nr_sequencia,
	nr_sequencia_autor,
	nr_atendimento,
	cd_procedimento,
	ie_origem_proced,
	qt_solicitada,
	qt_autorizada,
	ds_observacao,
	vl_autorizado,
	nr_prescricao,
	nr_seq_prescricao,
	nm_usuario_aprov,
	dt_aprovacao,
	nm_usuario,
	dt_atualizacao,
	nr_seq_proc_interno,
	nr_seq_agenda,
	nr_seq_agenda_proc)
SELECT	nr_sequencia_w,
	nr_sequencia_autor,
	nr_atendimento,
	cd_procedimento,
	ie_origem_proced,
	qt_solicidata_p,
	0,
	ds_observacao,
	vl_autorizado,
	nr_prescricao,
	nr_seq_prescricao,
	nm_usuario_aprov,
	dt_aprovacao,
	current_setting('gerar_pacote_autorizacao_pck.nm_usuario_w')::varchar(15),
	clock_timestamp(),
	nr_seq_proc_interno,
	nr_seq_agenda,
	nr_seq_agenda_proc
from	procedimento_autorizado
where	nr_sequencia = nr_seq_proc_autor_p;

insert 	into proc_autor_pacote(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_proc_autor,
	nr_seq_pacote_autor,
	qt_solicitada_pct,
	qt_solicitada_ant)
values (nextval('proc_autor_pacote_seq'),
	clock_timestamp(),
	current_setting('gerar_pacote_autorizacao_pck.nm_usuario_w')::varchar(15),
	nr_sequencia_w,
	nr_seq_pacote_p,
	0, --Gravo ambos com zero para saber que este item Ã© gerado do desdobramento e identificar ao desfazer o pacote.
	0);

END;

$body$
