SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE gerar_pacote_autorizacao_pck.desfazer_pacote () AS $body$
DECLARE


nr_seq_autor_pacote_w	bigint;
nr_seq_proc_autor_w	bigint;
nr_seq_mat_autor_w	bigint;
qt_solicitada_ant_w	double precision;
qt_solicitada_pct_w	double precision;

c01 CURSOR FOR
SELECT	nr_sequencia
from	autor_conv_pacote
where	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;

c02 CURSOR FOR
SELECT	nr_seq_proc_autor,
	qt_solicitada_ant,
	qt_solicitada_pct
from	proc_autor_pacote
where	nr_seq_pacote_autor	= nr_seq_autor_pacote_w;

c03 CURSOR FOR
SELECT	nr_seq_mat_autor,
	qt_solicitada_ant,
	qt_solicitada_pct
from	material_autor_pacote
where	nr_seq_pacote_autor	= nr_seq_autor_pacote_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_autor_pacote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_proc_autor_w,
		qt_solicitada_ant_w,
		qt_solicitada_pct_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		if (qt_solicitada_ant_w = 0) and --Excluo o item pois ele foi gerado pelo desdobrando do item devido ao pacote
			(qt_solicitada_pct_w = 0) then

			delete 	from procedimento_autorizado
			where	nr_sequencia		= nr_seq_proc_autor_w
			and	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;

		else
			update	procedimento_autorizado
			set	nr_seq_autor_pacote	 = NULL,
				qt_solicitada		= qt_solicitada_ant_w
			where	nr_sequencia		= nr_seq_proc_autor_w
			and	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;
		end if;

		end;
	end loop;
	close C02;

	open C03;
	loop
	fetch C03 into
		nr_seq_mat_autor_w,
		qt_solicitada_ant_w,
		qt_solicitada_pct_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin

		if (qt_solicitada_ant_w = 0) and --Excluo o item pois ele foi gerado pelo desdobrando do item devido ao pacote
			(qt_solicitada_pct_w = 0) then

			delete	from material_autorizado
			where	nr_sequencia		= nr_seq_mat_autor_w
			and	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;

		else
			update	material_autorizado
			set	nr_seq_autor_pacote	 = NULL,
				qt_solicitada		= qt_solicitada_ant_w
			where	nr_sequencia		= nr_seq_mat_autor_w
			and	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint;
		end if;

		end;
	end loop;
	close C03;

	delete	from autor_conv_pacote
	where	nr_sequencia_autor	= current_setting('gerar_pacote_autorizacao_pck.nr_sequencia_autor_w')::bigint
	and	nr_sequencia		= nr_seq_autor_pacote_w;

	end;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pacote_autorizacao_pck.desfazer_pacote () FROM PUBLIC;
