CREATE OR REPLACE PROCEDURE gerar_pacote_autorizacao_pck.gerar_proc_pacote (nr_seq_pacote_p bigint) AS $body$
DECLARE


i	integer;

c01 CURSOR FOR
SELECT	a.*
from	procedimento_autorizado a
where	a.nr_sequencia_autor	= nr_sequencia_autor_w
and	a.qt_solicitada		> 0;
c01_w	c01%rowtype;


BEGIN

current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt.delete;

i := 0;
open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	if (gerar_pacote_autorizacao_pck.obter_se_proc_pacote(	nr_seq_pacote_p,
					c01_w.cd_procedimento,
					c01_w.ie_origem_proced,
					i)) then

		current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[i].nr_sequencia		:= c01_w.nr_sequencia;
		current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[i].nr_seq_pacote		:= nr_seq_pacote_p;
		current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[i].cd_procedimento	:= c01_w.cd_procedimento;
		current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[i].ie_origem_proced	:= c01_w.ie_origem_proced;
		current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[i].qt_solicitada		:= c01_w.qt_solicitada;

		i := i + 1;
	end if;
end loop;
close C01;

end;

