CREATE OR REPLACE FUNCTION gerar_pacote_autorizacao_pck.obter_se_proc_pacote ( nr_seq_pacote_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_indice_p bigint) RETURNS boolean AS $body$
DECLARE


ie_pacote_w		boolean;
ie_acao_proc_autor_w	varchar(1);
qt_procedimento_w	convenio_pacote_autor_proc.qt_procedimento%type;


BEGIN

begin
select	ie_acao_proc_autor,
	qt_procedimento
into STRICT	ie_acao_proc_autor_w,
	qt_procedimento_w
from	convenio_pacote_autor_proc
where	nr_seq_pacote		= nr_seq_pacote_p
and	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	ie_situacao		= 'A'
and	current_setting('gerar_pacote_autorizacao_pck.dt_autorizacao_w')::timestamp	between coalesce(dt_inicio_vigencia,clock_timestamp() - interval '999 days') and coalesce(dt_fim_vigencia,clock_timestamp() + interval '999 days')  LIMIT 1;
exception
when others then
	ie_acao_proc_autor_w	:= 'E';
	qt_procedimento_w	:= 0;
end;


if (qt_procedimento_w > 0) then
	ie_pacote_w					:= true;
	current_setting('gerar_pacote_autorizacao_pck.proc_pacote_w')::proc_pacote_vt[nr_indice_p].qt_proc_pacote	:= qt_procedimento_w;
else
	ie_pacote_w	:= false;
end if;

return ie_pacote_w;

END;

