CREATE OR REPLACE PROCEDURE gerar_pacote_autorizacao_pck.set_pacote_excluido (nr_seq_pacote_p bigint) AS $body$
DECLARE

ds_pacote_w	varchar(255);

BEGIN

select	ds_pacote
into STRICT	ds_pacote_w
from	convenio_pacote_autor
where	nr_sequencia	= nr_seq_pacote_p;

if (current_setting('gerar_pacote_autorizacao_pck.ds_pacotes_nao_gerados_w')::coalesce(varchar(2000)::text, '') = '') then
