else
	PERFORM set_config('gerar_pacote_autorizacao_pck.ds_pacotes_nao_gerados_w', current_setting('gerar_pacote_autorizacao_pck.ds_pacotes_nao_gerados_w')::varchar(2000) 	||chr(130)||
						substr(nr_seq_pacote_p	|| ' - ' || ds_pacote_w,1,1999), false);
end if;

END;

