	SELECT	'' ds_diluicao, 
		cd_unidade_medida, 
		substr(obter_desc_solucao_adep(nr_prescricao_p, nr_seq_solucao),1,255) ds_item, 
		'SOL' ie_tipo_item, 
		ie_via_aplicacao, 
