				CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.addresumo(ds_diagnostico_long_w || ds_enter_w);
				end;
			end if;
	
			if (ds_cirurgia_long_w IS NOT NULL AND ds_cirurgia_long_w::text <> '') then	
				begin
				CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.addresumo(upper(wheb_mensagem_pck.get_texto(307207)) || ds_enter_w); -- CIRURGIAS
				CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.addresumo(ds_cirurgia_long_w || ds_enter_w);
				end;
			end if;
			
			if (ds_exames_long_w IS NOT NULL AND ds_exames_long_w::text <> '') then
				--gerar_resumo_oftalmo_pkg.addresumo('EXAMES'||ds_enter_w);                                             --O.S. 487586 _ABMACIEL

				CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.addresumo(ds_exames_long_w || ds_enter_w);
			end if;
			
			end;
		elsif (ds_tipo_w = wheb_mensagem_pck.get_texto(307145)) then -- Complemento
		
			Select  substr(Obter_dados_usuario_opcao(nm_usuario2_w,'DF'),1,60),
					substr(Obter_dados_usuario_opcao(nm_usuario2_w,'C'),1,60)					
			into STRICT	ds_funcao_w,
					cd_pessoa_usuario_w
			;
			
			ds_usuario_w := substr(nm_usuario2_w,1,80);
			
			if (cd_pessoa_usuario_w IS NOT NULL AND cd_pessoa_usuario_w::text <> '') then
			
				Select substr(max(coalesce(obter_dados_medico(cd_pessoa_usuario_w,'SGCRM'),obter_dados_pf(cd_pessoa_usuario_w,'CON')) || ':' ||
						coalesce(obter_dados_medico(cd_pessoa_usuario_w,'CRM'),obter_dados_pf(cd_pessoa_usuario_w,'CPR'))),1,20)
				into STRICT	ds_crm_usuario_w
				;
				
				if (ds_crm_usuario_w IS NOT NULL AND ds_crm_usuario_w::text <> '') then
					ds_usuario_w:= substr(ds_usuario_w||' ('||ds_crm_usuario_w||')',1,80);
				end if;
				
			end if;
			
			if (ds_funcao_w IS NOT NULL AND ds_funcao_w::text <> '') then
			
				ds_usuario_w := substr(ds_usuario_w || ' - '||ds_funcao_w,1,80);
		
			end if;
			

			ds_consulta_w	:= LPAD(wheb_mensagem_pck.get_texto(307542) || ': ',20,' ') || to_char(current_setting('gerar_resumo_oftalmo_pkg.dt_entrada_w')::timestamp,'dd/mm/yyyy') ||ds_enter_w; -- DATA COMPLEMENTO
			ds_consulta_w	:= ds_consulta_w ||LPAD(upper(wheb_mensagem_pck.get_texto(307145)) || ': ',20,' ')	||ds_classif_atend_w ||ds_enter_w; -- COMPLEMENTO
			ds_consulta_w	:= ds_consulta_w ||LPAD(upper(wheb_mensagem_pck.get_texto(307209)) || ': ',20,' ') || ds_usuario_w || ds_enter_w; -- USUARIO CADASTRO
			CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.addresumo(ds_consulta_w);
			ds_consulta_w	:= '';			
		end if;	
		end;
	end loop;
	close C01;

	CALL CALL CALL CALL gerar_resumo_oftalmo_pkg.gravar_resumo_banco(null);
	PERFORM set_config('gerar_resumo_oftalmo_pkg.ds_resumo_w', null, false);
	END;

$body$
