CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_param_consiste_saldo () RETURNS varchar AS $body$
DECLARE

		
		ie_cons_saldo_contrato_w	parametro_compras.ie_cons_saldo_contrato%type;
		
		
BEGIN
		
		ie_cons_saldo_contrato_w := 'S';
		
		select coalesce(max(ie_cons_saldo_contrato), 'S')
		  into STRICT ie_cons_saldo_contrato_w
		  from parametro_compras
		 where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento();
		
		return ie_cons_saldo_contrato_w;
		
	END;

