SET client_encoding TO 'UTF8';

	
	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Quantidade/Valor do item de solicitacoes de compra vinculadas ao contrato. <<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	/*
	Objetivo: Retornar o valor comprometido em solicitacoes de compra vinculadas ao contrato.
	Parametros: 
	nr_seq_contrato_p = Numero de sequencia do contrato.
	ie_qt_vl_p        = Quando passado 'QT': Ira retornar a quantidade total de itens presentes na solicitacao de compra, no qual o item e vinculado ao contrato.
	                    Quando passado 'VL': Ira retornar o valor total dos itens presentes na solicitacao de compra, no qual o item e vinculado ao contrato.
	ie_atual_p        = Quando 'S': Ira calcular a quantidade/valor atual comprometido em solicitacoes de compra.
					    Quando 'N': Ira retornar o valor de acordo com o ultimo gerenciamento de saldo do contrato existente na tabela "contrato_gerenciamento".
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_qt_vl_item_solic (nr_seq_contrato_p bigint, ie_qt_vl_p text, ie_atual_p text, nr_seq_regra_contrato_p bigint default null) RETURNS bigint AS $body$
DECLARE

		
		qt_item_solic contrato_gerenciamento.qt_solic_compra_item%type;
		vl_item_solic contrato_gerenciamento.vl_solic_compra%type;
					
		c01 CURSOR(nr_seq_contrato_c bigint) FOR
			SELECT a.nr_solic_compra nr_solic_compra
			  from solic_compra a
			 where coalesce(a.nr_seq_motivo_cancel::text, '') = ''
			   and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			   and exists (SELECT  1
							 from    solic_compra_item b
							where   b.nr_contrato = nr_seq_contrato_c
							  and	b.vl_unit_previsto > 0
							  and 	coalesce(b.dt_reprovacao::text, '') = ''
							  and	((coalesce(b.dt_baixa::text, '') = '' and coalesce(b.cd_motivo_baixa::text, '') = '')
							   or 	(((b.dt_baixa IS NOT NULL AND b.dt_baixa::text <> '') or (b.cd_motivo_baixa IS NOT NULL AND b.cd_motivo_baixa::text <> '')) 
							  and 	(b.qt_material - coalesce(b.qt_material_cancelado,0) > (select 	coalesce(sum(e.qt_prevista_entrega),0)
																						 from ordem_compra o,
																							  ordem_compra_item i,
																							  ordem_compra_item_entrega e
																						where	o.nr_ordem_compra = i.nr_ordem_compra
																						  and	i.nr_ordem_compra = e.nr_ordem_compra
																						  and	i.nr_item_oci = e.nr_item_oci
																						  and	i.nr_solic_compra = b.nr_solic_compra
																						  and	i.nr_item_solic_compra = b.nr_item_solic_compra
																						  and	(o.dt_liberacao IS NOT NULL AND o.dt_liberacao::text <> '')
																						  and 	coalesce(o.nr_seq_motivo_cancel::text, '') = ''
																						  and	coalesce(e.dt_cancelamento::text, '') = ''))))
							  and     a.nr_solic_compra = b.nr_solic_compra);
		
BEGIN
		
		qt_item_solic := 0;
		vl_item_solic := 0;
		
		if (ie_atual_p = 'N') then
			begin
				select a.qt_solic_compra_item,
					   a.vl_solic_compra
				  into STRICT qt_item_solic,
					   vl_item_solic
				  from contrato_gerenciamento a
				 where a.nr_seq_contrato = nr_seq_contrato_p
				   and a.dt_atualizacao = (SELECT max(b.dt_atualizacao)
											 from contrato_gerenciamento b
											where b.nr_seq_contrato = a.nr_seq_contrato);
			end;
		else
			begin
				For r01 in c01(nr_seq_contrato_p) loop
					if (ie_qt_vl_p = 'QT') then 
						qt_item_solic := qt_item_solic + gerenciamento_contrato_pck.obter_qt_vl_item_solicitacao(nr_seq_contrato_p, r01.nr_solic_compra, 'QT', nr_seq_regra_contrato_p);
					else
						vl_item_solic := vl_item_solic + gerenciamento_contrato_pck.obter_qt_vl_item_solicitacao(nr_seq_contrato_p, r01.nr_solic_compra, 'VL');
					end if;
				end loop;			
			end;
		end if;
		
		if (ie_qt_vl_p = 'QT') then
			return qt_item_solic;
		else
			return vl_item_solic;
		end if;
			
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION gerenciamento_contrato_pck.obter_qt_vl_item_solic (nr_seq_contrato_p bigint, ie_qt_vl_p text, ie_atual_p text, nr_seq_regra_contrato_p bigint default null) FROM PUBLIC;
