SET client_encoding TO 'UTF8';

	
	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>++++++ VALORES COMPROMETIDOS ++++++<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	
	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>> Quantidade/Valor do item da solicitacao de compra vinculado ao contrato.  <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	/*
	Objetivo: Retornar o valor comprometido da solcilitacao de compra vinculada ao contrato passada por parametro.
	Parametros: 
	nr_seq_contrato_p = Numero de sequencia do contrato.
	nr_solic_compra_p = Numero da solicitacao de compra.
	ie_qt_vl_p        = Quando passado 'QT': Ira retornar a quantidade total de itens presentes na solicitacao de compra, no qual o item e vinculado ao contrato.
	                    Quando passado 'VL': Ira retornar o valor total dos itens presentes na solicitacao de compra, no qual o item e vinculado ao contrato.
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_qt_vl_item_solicitacao (nr_seq_contrato_p bigint, nr_solic_compra_p bigint, ie_qt_vl_p text, nr_seq_regra_contrato_p bigint default null) RETURNS bigint AS $body$
DECLARE

	
	vl_comprometido_solic_w double precision;
	qt_comprometido_solic_w double precision;
	qt_material_ordem_w     double precision;
	
	c01 CURSOR(nr_seq_contrato_c bigint, nr_solic_compra_c bigint) FOR	
			SELECT	a.nr_solic_compra,
					b.nr_item_solic_compra,
					b.qt_material - coalesce(b.qt_material_cancelado,0) qt_material_solic,
					b.vl_unit_previsto
			from 	solic_compra a,
					solic_compra_item b
			where	a.nr_solic_compra = b.nr_solic_compra
			  and	b.nr_contrato = nr_seq_contrato_c
			  and   (((nr_seq_regra_contrato_p IS NOT NULL AND nr_seq_regra_contrato_p::text <> '') and nr_seq_regra_contrato_p > 0 and b.nr_seq_regra_contrato = nr_seq_regra_contrato_p) or
						coalesce(nr_seq_regra_contrato_p::text, '') = '')
			  and   b.nr_solic_compra = nr_solic_compra_c
			  and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			  and     coalesce(a.nr_seq_motivo_cancel::text, '') = ''
			  and	b.vl_unit_previsto > 0
			  and 	coalesce(b.dt_reprovacao::text, '') = ''
			  and	((coalesce(b.dt_baixa::text, '') = '' and coalesce(b.cd_motivo_baixa::text, '') = '')
			    or 	(((b.dt_baixa IS NOT NULL AND b.dt_baixa::text <> '') or (b.cd_motivo_baixa IS NOT NULL AND b.cd_motivo_baixa::text <> '')) 
			   and 	(b.qt_material - coalesce(b.qt_material_cancelado,0) > (	SELECT 	coalesce(sum(e.qt_prevista_entrega),0)
																		  from	ordem_compra o,
																				ordem_compra_item i,
																				ordem_compra_item_entrega e
																		 where	o.nr_ordem_compra = i.nr_ordem_compra
																		   and	i.nr_ordem_compra = e.nr_ordem_compra
																		   and	i.nr_item_oci = e.nr_item_oci
																		   and	i.nr_solic_compra = b.nr_solic_compra
																		   and	i.nr_item_solic_compra = b.nr_item_solic_compra
																		   and	(o.dt_liberacao IS NOT NULL AND o.dt_liberacao::text <> '')
																		   and 	coalesce(o.nr_seq_motivo_cancel::text, '') = ''
																		   and	coalesce(e.dt_cancelamento::text, '') = ''))))
			order by 1;
	
	
BEGIN
	
		vl_comprometido_solic_w := 0;
		qt_comprometido_solic_w := 0;	
		
		For r01 in c01(nr_seq_contrato_p, nr_solic_compra_p) loop
		
			select  coalesce(sum(c.qt_prevista_entrega),0)
						into STRICT	qt_material_ordem_w
			  from  ordem_compra a,
					ordem_compra_item b,
					ordem_compra_item_entrega c
			 where a.nr_ordem_compra = b.nr_ordem_compra
			   and b.nr_ordem_compra = c.nr_ordem_compra
			   and b.nr_item_oci     = c.nr_item_oci
			   and coalesce(c.dt_cancelamento::text, '') = ''
			   and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			   and coalesce(b.dt_reprovacao::text, '') = ''
			   and b.nr_contrato     = nr_seq_contrato_p
			   and b.nr_solic_compra = nr_solic_compra_p
			   and b.nr_item_solic_compra = r01.nr_item_solic_compra
			   and coalesce(a.nr_seq_motivo_cancel::text, '') = ''
			 order by 1;
						
				/* As quantidades existentes em solicitacoes que compra que ja possuem ordem de compra gerada devem ser desconsideradas */

				if (qt_material_ordem_w <> 0) and (r01.qt_material_solic > qt_material_ordem_w) then
					vl_comprometido_solic_w := vl_comprometido_solic_w + ((r01.qt_material_solic - qt_material_ordem_w) * r01.vl_unit_previsto);
					qt_comprometido_solic_w := qt_comprometido_solic_w + (r01.qt_material_solic - qt_material_ordem_w);
				elsif (qt_material_ordem_w = 0) then
					vl_comprometido_solic_w := vl_comprometido_solic_w + (r01.qt_material_solic * r01.vl_unit_previsto);
					qt_comprometido_solic_w := qt_comprometido_solic_w + r01.qt_material_solic;
				end if;	
		end loop;
	
	if (ie_qt_vl_p = 'QT') then
		return qt_comprometido_solic_w;
	else 	
		return vl_comprometido_solic_w;
	end if;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
